const menu=document.querySelector(".nav__menu-toggler"),navLinks=document.querySelector(".nav__links"),navLinksLink=navLinks.querySelectorAll("ul li");menu.addEventListener("click",()=>{menu.classList.toggle("open"),navLinks.classList.toggle("active-nav-links")});const links=[...navLinksLink];links.map(n=>{n.addEventListener("click",()=>{menu.classList.remove("open"),navLinks.classList.remove("active-nav-links")})});